package org.springframework.demo;import org.springframework.demo.Employees.Commission;import org.springframework.demo.Employees.Employee;import org.springframework.demo.Employees.Hourly;import org.springframework.demo.Employees.SalariedCommission;public class SalaryService {    public Employee calculateSalary(Hourly employee) {        Double newSalary;        if (employee.getWorkHour() > 40) {            newSalary = 40.0 * employee.getSalaryHour() + (employee.getWorkHour() - 40) * 1.5 * employee.getSalaryHour();        } else {            newSalary = employee.getWorkHour() * employee.getSalaryHour();        }        employee.setSalary(newSalary);        return employee;    }    public Employee calculateSalary(Commission employee, Double amount) {        Double bonus = amount * employee.getPercentageSales() / 100;        employee.setSalary(employee.getSalary() + bonus);        return employee;    }    public Employee calculateSalary(SalariedCommission employee, Double amount) {        Double bonus = amount * employee.getPercentageSales() / 100;        employee.setSalary(employee.getSalary() + bonus);        return employee;    }}